USE db_test;create table sampleTable(id int,sampleName varchar(10));GOinsert into sampleTable values(10,'data');GOIF EXISTS(	SELECT 1 FROM sys.objects WHERE type = 'F' AND OBJECT_ID = OBJECT_ID('[dbo].[fn_executeDMLQueries]'))BEGIN	DROP Function [dbo].[fn_executeDMLQueries]ENDGOCREATE FUNCTION [dbo].[fn_executeDMLQueries](	@id INT)RETURNS INTAS BEGIN	UPDATE sampleTable SET sampleName='anju' where id=@id;	RETURN 1ENDGOSELECT * FROM [fn_executeDMLQueries](10);